mapper.select:
SELECT id, name, resource_type_id, description, status, attributes, created_by, created_at, updated_at, version
FROM resource


        AND resource_type_id = #{resourceTypeId}


        AND status = #{status}


        AND (name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))


ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM resource


        AND resource_type_id = #{resourceTypeId}


        AND status = #{status}


        AND (name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))

SELECT id, name, resource_type_id, description, status, attributes, created_by, created_at, updated_at, version
FROM resource
WHERE created_by = #{createdBy}
ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE resource
SET status = #{status}, version = version + 1, updated_at = NOW()
WHERE id = #{id} AND version = #{version}

